mapper.select:
select user_id, name, email, file
from user
where user_id = #{userId}

mapper.update:
update user
set access_token  = #{accessToken},
    refresh_token = #{refreshToken}
where user_id = #{userId}

update user
set access_token = null
where access_token = #{accessToken}

update user
set file = #{file}
where user_id = #{userId}
